public class Punto7App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*7) Muestra los números del 1 al 100 (ambos incluidos). Usa un bucle while.
		 */
		int num=1;
		while(num<=100) {
			System.out.println(num);
			num++;
		}

	}

}
